import React, { useState, useEffect } from 'react';
import './App.css';
import AudioRecorder from './components/AudioRecorder';
import FileUpload from './components/FileUpload';
import TranscriptionResults from './components/TranscriptionResults';
import History from './components/History';
import Statistics from './components/Statistics';
import Settings from './components/Settings';
import { transcribeAudio, checkHealth } from './services/api';
import { 
  Mic, 
  FileAudio, 
  Settings as SettingsIcon, 
  History as HistoryIcon,
  BarChart3,
  Upload,
  Activity
} from 'lucide-react';

function App() {
  const [activeTab, setActiveTab] = useState('record');
  const [transcriptions, setTranscriptions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [backendStatus, setBackendStatus] = useState('checking');
  const [settings, setSettings] = useState({
    provider: 'azure',
    language: 'en-US',
    enableDiarization: true,
    maxSpeakers: 4,
    includeTimestamps: true,
    showCost: true
  });
  const [showSettings, setShowSettings] = useState(false);

  useEffect(() => {
    // Check backend health on mount
    checkBackendHealth();
    const interval = setInterval(checkBackendHealth, 30000); // Check every 30s
    return () => clearInterval(interval);
  }, []);

  const checkBackendHealth = async () => {
    const health = await checkHealth();
    setBackendStatus(health.status === 'healthy' ? 'healthy' : 'unhealthy');
  };

  const handleAudioRecorded = async (audioBlob, fileName = 'recording.wav') => {
    setIsLoading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('file', audioBlob, fileName);
      formData.append('provider', settings.provider);
      formData.append('language', settings.language);
      formData.append('enable_diarization', settings.enableDiarization);
      formData.append('max_speakers', settings.maxSpeakers);
      formData.append('include_timestamps', settings.includeTimestamps);

      const result = await transcribeAudio(formData);

      const newTranscription = {
        ...result,
        timestamp: new Date().toISOString(),
        fileName: fileName,
        audioUrl: audioBlob instanceof Blob ? URL.createObjectURL(audioBlob) : null
      };

      setTranscriptions([newTranscription, ...transcriptions]);

      // Switch to results tab after successful transcription
      if (activeTab === 'record' || activeTab === 'upload') {
        setActiveTab('results');
      }
    } catch (err) {
      setError(err.message || 'Failed to transcribe audio');
    } finally {
      setIsLoading(false);
    }
  };

  const tabs = [
    { id: 'record', label: 'Record', icon: Mic },
    { id: 'upload', label: 'Upload', icon: Upload },
    { id: 'results', label: 'Results', icon: FileAudio },
    { id: 'history', label: 'History', icon: HistoryIcon },
    { id: 'stats', label: 'Statistics', icon: BarChart3 }
  ];

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="logo">
            <Mic className="logo-icon" />
            <h1>Speecher</h1>
          </div>
          <p className="subtitle">Multi-Cloud Audio Transcription Service</p>
        </div>

        <div className="header-actions">
          <div className={`backend-status ${backendStatus}`}>
            <Activity size={16} />
            <span>Backend: {backendStatus}</span>
          </div>
          <button 
            className="settings-btn"
            onClick={() => setShowSettings(!showSettings)}
          >
            <SettingsIcon size={20} />
          </button>
        </div>
      </header>

      <nav className="App-nav">
        {tabs.map(tab => {
          const Icon = tab.icon;
          return (
            <button
              key={tab.id}
              className={`nav-tab ${activeTab === tab.id ? 'active' : ''}`}
              onClick={() => setActiveTab(tab.id)}
            >
              <Icon size={20} />
              <span>{tab.label}</span>
            </button>
          );
        })}
      </nav>

      <main className="App-main">
        {showSettings && (
          <Settings 
            settings={settings} 
            onSettingsChange={setSettings}
            onClose={() => setShowSettings(false)}
          />
        )}

        <div className="container">
          {activeTab === 'record' && (
            <div className="tab-content">
              <div className="tab-header">
                <h2>🎤 Record Audio</h2>
                <p>Record directly from your microphone</p>
              </div>
              <AudioRecorder 
                onAudioRecorded={handleAudioRecorded}
                isLoading={isLoading}
              />
            </div>
          )}

          {activeTab === 'upload' && (
            <div className="tab-content">
              <div className="tab-header">
                <h2>📁 Upload Files</h2>
                <p>Upload audio files for transcription</p>
              </div>
              <FileUpload
                onFilesUploaded={handleAudioRecorded}
                isLoading={isLoading}
                settings={settings}
              />
            </div>
          )}

          {activeTab === 'results' && (
            <div className="tab-content">
              <div className="tab-header">
                <h2>📝 Transcription Results</h2>
                <p>View and export your transcriptions</p>
              </div>
              <TranscriptionResults 
                transcriptions={transcriptions}
                settings={settings}
              />
            </div>
          )}

          {activeTab === 'history' && (
            <div className="tab-content">
              <div className="tab-header">
                <h2>📜 History</h2>
                <p>Browse all your past transcriptions</p>
              </div>
              <History 
                onSelectTranscription={(t) => {
                  setTranscriptions([t, ...transcriptions]);
                  setActiveTab('results');
                }}
              />
            </div>
          )}

          {activeTab === 'stats' && (
            <div className="tab-content">
              <div className="tab-header">
                <h2>📊 Statistics</h2>
                <p>Usage analytics and insights</p>
              </div>
              <Statistics />
            </div>
          )}

          {error && (
            <div className="error-message">
              <span>❌</span> {error}
              <button onClick={() => setError(null)}>×</button>
            </div>
          )}

          {isLoading && (
            <div className="loading-overlay">
              <div className="loading">
                <div className="spinner"></div>
                <p>Transcribing audio...</p>
                <p className="loading-tip">This may take a moment depending on file size</p>
              </div>
            </div>
          )}
        </div>
      </main>

      <footer className="App-footer">
        <div className="footer-content">
          <p>© 2024 Speecher | Multi-cloud Speech-to-Text</p>
          <div className="footer-links">
            <a href="https://github.com/yourusername/speecher" target="_blank" rel="noopener noreferrer">GitHub</a>
            <span>•</span>
            <a href="/docs" target="_blank">API Docs</a>
            <span>•</span>
            <span>v0.1.0</span>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
