import React, { useState } from 'react';
import './App.css';
import AudioRecorder from './components/AudioRecorder';
import TranscriptionResults from './components/TranscriptionResults';
import Settings from './components/Settings';
import { transcribeAudio } from './services/api';
import { Mic, FileAudio, Settings as SettingsIcon } from 'lucide-react';

function App() {
  const [transcriptions, setTranscriptions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [settings, setSettings] = useState({
    provider: 'azure',
    language: 'en-US',
    enableDiarization: true,
    maxSpeakers: 4
  });
  const [showSettings, setShowSettings] = useState(false);

  const handleAudioRecorded = async (audioBlob) => {
    setIsLoading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('file', audioBlob, 'recording.wav');
      formData.append('provider', settings.provider);
      formData.append('language', settings.language);
      formData.append('enable_diarization', settings.enableDiarization);
      formData.append('max_speakers', settings.maxSpeakers);

      const result = await transcribeAudio(formData);

      setTranscriptions([
        {
          ...result,
          timestamp: new Date().toISOString(),
          audioUrl: URL.createObjectURL(audioBlob)
        },
        ...transcriptions
      ]);
    } catch (err) {
      setError(err.message || 'Failed to transcribe audio');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="header-content">
          <div className="logo">
            <Mic className="logo-icon" />
            <h1>Speecher</h1>
          </div>
          <p className="subtitle">Real-time Audio Transcription</p>
        </div>
        <button 
          className="settings-btn"
          onClick={() => setShowSettings(!showSettings)}
        >
          <SettingsIcon size={20} />
        </button>
      </header>

      <main className="App-main">
        {showSettings && (
          <Settings 
            settings={settings} 
            onSettingsChange={setSettings}
            onClose={() => setShowSettings(false)}
          />
        )}

        <div className="container">
          <div className="recorder-section">
            <AudioRecorder 
              onAudioRecorded={handleAudioRecorded}
              isLoading={isLoading}
            />

            {error && (
              <div className="error-message">
                <span>❌</span> {error}
              </div>
            )}

            {isLoading && (
              <div className="loading">
                <div className="spinner"></div>
                <p>Transcribing audio...</p>
              </div>
            )}
          </div>

          <div className="results-section">
            <h2>
              <FileAudio size={20} />
              Transcription Results
            </h2>
            <TranscriptionResults transcriptions={transcriptions} />
          </div>
        </div>
      </main>

      <footer className="App-footer">
        <p>© 2024 Speecher | Multi-cloud Speech-to-Text</p>
      </footer>
    </div>
  );
}

export default App;
