import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import {
  AppBar,
  Toolbar,
  Typography,
  Container,
  Box,
  Tabs,
  Tab,
  Paper,
  IconButton,
  Chip,
  Drawer,
  Alert,
  Snackbar,
  CircularProgress,
  Backdrop
} from '@mui/material';
import {
  Mic as MicIcon,
  CloudUpload as UploadIcon,
  Description as FileAudioIcon,
  History as HistoryIcon,
  BarChart as BarChartIcon,
  Settings as SettingsIcon,
  GraphicEq as LogoIcon,
  CheckCircle as CheckIcon,
  Error as ErrorIcon,
  Refresh as RefreshIcon
} from '@mui/icons-material';

import AudioRecorder from './components/AudioRecorder';
import FileUpload from './components/FileUpload';
import TranscriptionResults from './components/TranscriptionResults';
import History from './components/History';
import Statistics from './components/Statistics';
import Settings from './components/Settings';
import { transcribeAudio, checkHealth } from './services/api';

// Create MUI theme
const theme = createTheme({
  palette: {
    primary: {
      main: '#667eea',
      light: '#8b9bf0',
      dark: '#4c5cd8',
    },
    secondary: {
      main: '#764ba2',
      light: '#9166bc',
      dark: '#5a3a7e',
    },
    success: {
      main: '#48bb78',
    },
    warning: {
      main: '#ed8936',
    },
    error: {
      main: '#f56565',
    },
    background: {
      default: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      paper: '#ffffff',
    },
  },
  typography: {
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif',
    h4: {
      fontWeight: 700,
    },
  },
  shape: {
    borderRadius: 12,
  },
  components: {
    MuiPaper: {
      styleOverrides: {
        root: {
          backgroundImage: 'none',
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          fontWeight: 600,
        },
      },
    },
    MuiTab: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          fontWeight: 600,
        },
      },
    },
  },
});

// Tab panel component
function TabPanel({ children, value, index, ...other }) {
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`tabpanel-${index}`}
      aria-labelledby={`tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ py: 3 }}>
          {children}
        </Box>
      )}
    </div>
  );
}

function App() {
  const [activeTab, setActiveTab] = useState(0);
  const [transcriptions, setTranscriptions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [backendStatus, setBackendStatus] = useState('checking');
  const [settings, setSettings] = useState({
    provider: 'azure',
    language: 'en-US',
    enableDiarization: true,
    maxSpeakers: 4,
    includeTimestamps: true,
    showCost: true
  });
  const [showSettings, setShowSettings] = useState(false);

  useEffect(() => {
    // Check backend health on mount
    checkBackendHealth();
    const interval = setInterval(checkBackendHealth, 30000); // Check every 30s
    return () => clearInterval(interval);
  }, []);

  const checkBackendHealth = async () => {
    const health = await checkHealth();
    setBackendStatus(health.status === 'healthy' ? 'healthy' : 'unhealthy');
  };

  const handleAudioRecorded = async (audioBlob, fileName = 'recording.wav') => {
    setIsLoading(true);
    setError(null);

    try {
      const formData = new FormData();
      formData.append('file', audioBlob, fileName);
      formData.append('provider', settings.provider);
      formData.append('language', settings.language);
      formData.append('enable_diarization', settings.enableDiarization);
      formData.append('max_speakers', settings.maxSpeakers);
      formData.append('include_timestamps', settings.includeTimestamps);

      const result = await transcribeAudio(formData);

      const newTranscription = {
        ...result,
        timestamp: new Date().toISOString(),
        fileName: fileName,
        audioUrl: audioBlob instanceof Blob ? URL.createObjectURL(audioBlob) : null
      };

      setTranscriptions([newTranscription, ...transcriptions]);

      // Switch to results tab after successful transcription
      if (activeTab === 'record' || activeTab === 'upload') {
        setActiveTab('results');
      }
    } catch (err) {
      setError(err.message || 'Failed to transcribe audio');
    } finally {
      setIsLoading(false);
    }
  };

  const getStatusColor = () => {
    switch (backendStatus) {
      case 'healthy': return 'success';
      case 'unhealthy': return 'error';
      default: return 'warning';
    }
  };

  const getStatusIcon = () => {
    switch (backendStatus) {
      case 'healthy': return <CheckIcon fontSize="small" />;
      case 'unhealthy': return <ErrorIcon fontSize="small" />;
      default: return <CircularProgress size={16} />;
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box
        sx={{
          minHeight: '100vh',
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        }}
      >
        {/* App Bar */}
        <AppBar position="static" color="transparent" elevation={0}>
          <Toolbar sx={{ backgroundColor: 'rgba(255, 255, 255, 0.95)', backdropFilter: 'blur(10px)' }}>
            <LogoIcon sx={{ fontSize: 40, color: 'primary.main', mr: 2 }} />
            <Box sx={{ flexGrow: 1 }}>
              <Typography variant="h4" component="h1" sx={{ color: 'text.primary' }}>
                Speecher
              </Typography>
              <Typography variant="caption" sx={{ color: 'text.secondary' }}>
                AI-Powered Speech Recognition Platform
              </Typography>
            </Box>

            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Chip
                icon={getStatusIcon()}
                label={`Backend: ${backendStatus}`}
                color={getStatusColor()}
                size="small"
                variant="outlined"
              />
              <IconButton color="primary" onClick={() => checkBackendHealth()}>
                <RefreshIcon />
              </IconButton>
              <IconButton color="primary" onClick={() => setShowSettings(true)}>
                <SettingsIcon />
              </IconButton>
            </Box>
          </Toolbar>
        </AppBar>

        {/* Main Content */}
        <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
          <Paper elevation={3} sx={{ borderRadius: 2 }}>
            <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
              <Tabs 
                value={activeTab} 
                onChange={(e, newValue) => setActiveTab(newValue)}
                variant="fullWidth"
              >
                <Tab icon={<MicIcon />} label="Record" />
                <Tab icon={<UploadIcon />} label="Upload" />
                <Tab icon={<FileAudioIcon />} label="Results" />
                <Tab icon={<HistoryIcon />} label="History" />
                <Tab icon={<BarChartIcon />} label="Statistics" />
              </Tabs>
            </Box>

            <TabPanel value={activeTab} index={0}>
              <AudioRecorder 
                onAudioRecorded={handleAudioRecorded}
                isLoading={isLoading}
                settings={settings}
              />
            </TabPanel>

            <TabPanel value={activeTab} index={1}>
              <FileUpload 
                onFilesUploaded={handleAudioRecorded}
                isLoading={isLoading}
                settings={settings}
              />
            </TabPanel>

            <TabPanel value={activeTab} index={2}>
              <TranscriptionResults 
                transcriptions={transcriptions}
                settings={settings}
              />
            </TabPanel>

            <TabPanel value={activeTab} index={3}>
              <History 
                onSelectTranscription={(t) => {
                  setTranscriptions([t, ...transcriptions]);
                  setActiveTab(2);
                }}
              />
            </TabPanel>

            <TabPanel value={activeTab} index={4}>
              <Statistics />
            </TabPanel>
          </Paper>
        </Container>

        {/* Settings Drawer */}
        <Drawer
          anchor="right"
          open={showSettings}
          onClose={() => setShowSettings(false)}
        >
          <Settings 
            settings={settings}
            onSettingsChange={setSettings}
            onClose={() => setShowSettings(false)}
          />
        </Drawer>

        {/* Error Snackbar */}
        <Snackbar 
          open={!!error} 
          autoHideDuration={6000} 
          onClose={() => setError(null)}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
        >
          <Alert onClose={() => setError(null)} severity="error" sx={{ width: '100%' }}>
            {error}
          </Alert>
        </Snackbar>

        {/* Loading Backdrop */}
        <Backdrop
          sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
          open={isLoading}
        >
          <CircularProgress color="inherit" />
        </Backdrop>
      </Box>
    </ThemeProvider>
  );
}

export default App;
