import React, { useState, useRef, useEffect } from 'react';
import { Mic, Square, Play, Pause, Download, Trash2 } from 'lucide-react';
import WaveSurfer from 'wavesurfer.js';
import './AudioRecorder.css';

const AudioRecorder = ({ onAudioRecorded, isLoading }) => {
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [audioUrl, setAudioUrl] = useState(null);
  const [duration, setDuration] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);

  const mediaRecorderRef = useRef(null);
  const streamRef = useRef(null);
  const chunksRef = useRef([]);
  const timerRef = useRef(null);
  const wavesurferRef = useRef(null);
  const waveformRef = useRef(null);

  useEffect(() => {
    // Initialize WaveSurfer when we have audio
    if (audioUrl && waveformRef.current && !wavesurferRef.current) {
      wavesurferRef.current = WaveSurfer.create({
        container: waveformRef.current,
        waveColor: '#4F46E5',
        progressColor: '#818CF8',
        cursorColor: '#4F46E5',
        barWidth: 2,
        barRadius: 3,
        responsive: true,
        height: 60,
        normalize: true,
        backend: 'WebAudio'
      });

      wavesurferRef.current.load(audioUrl);

      wavesurferRef.current.on('finish', () => {
        setIsPlaying(false);
      });
    }

    return () => {
      if (wavesurferRef.current) {
        wavesurferRef.current.destroy();
        wavesurferRef.current = null;
      }
    };
  }, [audioUrl]);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        } 
      });

      streamRef.current = stream;

      const mediaRecorder = new MediaRecorder(stream, {
        mimeType: 'audio/webm'
      });

      mediaRecorderRef.current = mediaRecorder;
      chunksRef.current = [];

      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          chunksRef.current.push(event.data);
        }
      };

      mediaRecorder.onstop = async () => {
        const audioBlob = new Blob(chunksRef.current, { type: 'audio/webm' });
        const audioUrl = URL.createObjectURL(audioBlob);

        // Convert to WAV for better compatibility
        const wavBlob = await convertToWav(audioBlob);

        setAudioBlob(wavBlob);
        setAudioUrl(audioUrl);
        setIsRecording(false);

        // Clean up waveform for new recording
        if (wavesurferRef.current) {
          wavesurferRef.current.destroy();
          wavesurferRef.current = null;
        }
      };

      mediaRecorder.start(100); // Collect data every 100ms
      setIsRecording(true);
      setDuration(0);

      // Start duration timer
      timerRef.current = setInterval(() => {
        setDuration(prev => prev + 1);
      }, 1000);

    } catch (error) {
      console.error('Error accessing microphone:', error);
      alert('Unable to access microphone. Please check permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
      mediaRecorderRef.current.stop();

      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }

      if (timerRef.current) {
        clearInterval(timerRef.current);
      }

      setIsRecording(false);
    }
  };

  const convertToWav = async (webmBlob) => {
    // For now, return the webm blob
    // In production, you'd convert to WAV using AudioContext
    return webmBlob;
  };

  const togglePlayback = () => {
    if (wavesurferRef.current) {
      if (isPlaying) {
        wavesurferRef.current.pause();
      } else {
        wavesurferRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const clearRecording = () => {
    setAudioBlob(null);
    setAudioUrl(null);
    setDuration(0);
    setIsPlaying(false);

    if (wavesurferRef.current) {
      wavesurferRef.current.destroy();
      wavesurferRef.current = null;
    }
  };

  const downloadRecording = () => {
    if (audioBlob) {
      const url = URL.createObjectURL(audioBlob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `recording_${new Date().toISOString()}.webm`;
      a.click();
      URL.revokeObjectURL(url);
    }
  };

  const submitForTranscription = () => {
    if (audioBlob && onAudioRecorded) {
      onAudioRecorded(audioBlob);
    }
  };

  const formatDuration = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="audio-recorder">
      <div className="recorder-controls">
        {!isRecording && !audioBlob && (
          <button 
            className="record-btn start"
            onClick={startRecording}
            disabled={isLoading}
          >
            <Mic size={24} />
            <span>Start Recording</span>
          </button>
        )}

        {isRecording && (
          <>
            <button 
              className="record-btn stop"
              onClick={stopRecording}
            >
              <Square size={24} />
              <span>Stop Recording</span>
            </button>
            <div className="recording-indicator">
              <span className="pulse"></span>
              <span className="timer">{formatDuration(duration)}</span>
            </div>
          </>
        )}

        {audioBlob && !isRecording && (
          <div className="playback-controls">
            <button 
              className="control-btn"
              onClick={togglePlayback}
            >
              {isPlaying ? <Pause size={20} /> : <Play size={20} />}
            </button>

            <div className="waveform" ref={waveformRef}></div>

            <span className="duration">{formatDuration(duration)}</span>

            <button 
              className="control-btn"
              onClick={downloadRecording}
              title="Download recording"
            >
              <Download size={20} />
            </button>

            <button 
              className="control-btn"
              onClick={clearRecording}
              title="Clear recording"
            >
              <Trash2 size={20} />
            </button>

            <button 
              className="submit-btn"
              onClick={submitForTranscription}
              disabled={isLoading}
            >
              {isLoading ? 'Processing...' : 'Transcribe'}
            </button>
          </div>
        )}
      </div>

      {isRecording && (
        <div className="recording-tips">
          <p>ðŸŽ¤ Speak clearly into your microphone</p>
        </div>
      )}
    </div>
  );
};

export default AudioRecorder;
